INSERT INTO `oc_t_pages_description` (`fk_i_pages_id`, `fk_c_locale_code`, `s_title`, `s_text`) VALUES
(1, 'fr_FR', '{WEB_TITLE} - R&eacute;ponse pour votre annonce {ITEM_TITLE}', '&lt;p&gt;Bonjour {CONTACT_NAME}!&lt;/p&gt;\r\n&lt;p&gt;{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) vous a laiss&eacute; un message sur votre annonce {ITEM_TITLE}:&lt;/p&gt;\r\n&lt;p&gt;{COMMENT}&lt;/p&gt;\n&lt;p&gt;Merci,&lt;/p&gt;\n&lt;p&gt;{WEB_TITLE}&lt;/p&gt;'),
(2, 'fr_FR', 'Activation de votre compte {WEB_TITLE}', '&lt;p&gt;Bonjour {USER_NAME},&lt;/p&gt;\r\n&lt;p&gt;Pour confirmer l\'activation de votre compte, merci de cliquer sur ce lien: {VALIDATION_LINK}&lt;/p&gt;\r\n&lt;p&gt;Merci&lt;/p&gt;\r\n&lt;p&gt;{WEB_TITLE}&lt;/p&gt;'),
(3, 'fr_FR', '{WEB_TITLE} - Inscription r&eacute;ussie!', '&lt;p&gt;Bonjour {USER_NAME},&lt;/p&gt;\r\n&lt;p&gt;Vous avez valider votre inscription avec succ&egrave;s.&lt;/p&gt;\r\n&lt;p&gt;Merci!&lt;/p&gt;\r\n&lt;p&gt;{WEB_TITLE}&lt;/p&gt;'),
(4, 'fr_FR', 'Regarde ce que j\'ai d&eacute;couvert sur {WEB_TITLE} !', '&lt;p&gt;Bonjour {FRIEND_NAME},&lt;/p&gt;\r\n&lt;p&gt;Votre ami {USER_NAME} veut partager ce lien avec vous::&lt;/p&gt;\r\n&lt;p&gt;{ITEM_URL}&lt;/p&gt;\r\n&lt;p&gt;Message:&lt;/p&gt;\r\n&lt;p&gt;{COMMENT}&lt;/p&gt;\r\n&lt;p&gt;{WEB_TITLE}&lt;/p&gt;'),
(5, 'fr_FR', '{WEB_TITLE} - Nouvelles annonces dans la derni&egrave;re heure...', '&lt;p&gt;Bonjour {USER_NAME},&lt;/p&gt;\r\n&lt;p&gt;Des annonces ont &eacute;t&eacute; publi&eacute;s dans la derni&egrave;re heure. Jetez un oeil :&lt;/p&gt;\r\n&lt;p&gt;{ADS}&lt;/p&gt;\r\n&lt;p&gt;-------------&lt;/p&gt;\r\n&lt;p&gt;Pour vous d&eacute;sabonner de cette alerte, cliquez sur: {UNSUB_LINK}&lt;/p&gt;'),
(6, 'fr_FR', '{WEB_TITLE} - Nouvelles annonces de la journ&eacute;e', '&lt;p&gt;Bonjour {USER_NAME},&lt;/p&gt;\r\n&lt;p&gt;Des annonces ont &eacute;t&eacute; publi&eacute;s dans les derni&egrave;res 24 heures. Jetez un oeil:&lt;/p&gt;\r\n&lt;p&gt;{ADS}&lt;/p&gt;\r\n&lt;p&gt;-------------&lt;/p&gt;\r\n&lt;p&gt;Pour vous d&eacute;sabonner de cette alerte, cliquez sur: {UNSUB_LINK}&lt;/p&gt;'),
(7, 'fr_FR', '{WEB_TITLE} - Nouvelles annonces de la derni&egrave;re semaine', '&lt;p&gt;Bonjour {USER_NAME},&lt;/p&gt;\r\n&lt;p&gt;Des annonces ont &eacute;t&eacute; publi&eacute;s dans la derni&egrave;re semaine. Jetez un oeil:&lt;/p&gt;\r\n&lt;p&gt;{ADS}&lt;/p&gt;\r\n&lt;p&gt;-------------&lt;/p&gt;\r\n&lt;p&gt;Pour vous d&eacute;sabonner de cette alerte, cliquez sur: {UNSUB_LINK}&lt;/p&gt;'),
(8, 'fr_FR', '{WEB_TITLE} - Nouvelle annonce', '&lt;p&gt;Bonjour {USER_NAME},&lt;/p&gt;\r\n&lt;p&gt;Une nouvelle annonce a &eacute;t&eacute; publi&eacute;e. Jetez un oeil:&lt;/p&gt;\r\n&lt;p&gt;{ADS}&lt;/p&gt;\r\n&lt;p&gt;-------------&lt;/p&gt;\r\n&lt;p&gt;Pour vous d&eacute;sabonner de cette alerte, cliquez sur: {UNSUB_LINK}&lt;/p&gt;'),
(9, 'fr_FR', '{WEB_TITLE} - Nouveau commentaire sur l\'annonce {ITEM_ID}', '&lt;p&gt; Il ya un nouveau commentaire sur l\'annonce {ITEM_ID} &lt;br /&gt;URL: {ITEM_URL}&lt;/p&gt;\r\n&lt;p&gt;Titre: {COMMENT_TITLE}&lt;br /&gt;Commentaire: {COMMENT_TEXT}&lt;br /&gt;Auteur: {COMMENT_AUTHOR}&lt;br /&gt;courriel de l\'auteur: {COMMENT_EMAIL}&lt;/p&gt;'),
(10, 'fr_FR', '{WEB_TITLE} - Modifier les options pour l\'annonce {ITEM_ID}', '&lt;p&gt;Bonjour {USER_NAME},&lt;/p&gt;\r\n&lt;div&gt;\r\n&lt;p&gt; Vous n\'&ecirc;tes pas inscrit &agrave; {WEB_TITLE}, mais vous pouvez toujours modifier ou supprimer l\'annonce {ITEM_TITLE} pour une courte dur&eacute;e.&lt;/p&gt;\r\n&lt;p&gt; Vous pouvez modifier votre annonce en suivant ce lien: {EDIT_LINK}&lt;/p&gt;\r\n&lt;p&gt; Vous pouvez supprimer votre annonce en suivant ce lien: {DELETE_LINK}&lt;/p&gt;\r\n&lt;p&gt; Si vous vous inscrivez, vous aurez un acc&egrave;s complet aux options d\'&eacute;dition.&lt;/p&gt;\r\n&lt;p&gt;&nbsp;&lt;/p&gt;\r\n{WEB_TITLE}&lt;/div&gt;'),
(11, 'fr_FR', '{WEB_TITLE} - Valider votre annonce', '&lt;p&gt;Bonjour {USER_NAME},&lt;/p&gt;\r\n&lt;p&gt; Vous recevez ce courriel parce qu\'une annonce a &eacute;t&eacute; publi&eacute;e sur {WEB_TITLE}.&lt;/p&gt;\r\n&lt;p&gt;Valider cette annonce en cliquant sur ​​le lien &agrave; la fin de ce courriel. Si vous n\'avez pas de publier cette annonce, merci d\'ignorer ce courriel&lt;/p&gt;\r\n&lt;p&gt;D&eacute;tails de l\'annonce:&lt;/p&gt;\r\n&lt;p&gt;Contact: {USER_NAME}&lt;br /&gt;Courriel du vendeur: {USER_EMAIL}&lt;/p&gt;\r\n&lt;p&gt;{ITEM_DESCRIPTION_ALL_LANGUAGES}&lt;/p&gt;\r\n&lt;p&gt;Prix: {ITEM_PRICE}&lt;br /&gt;R&eacute;gion (Province): {ITEM_REGION}&lt;br /&gt;Ville: {ITEM_CITY}&lt;br /&gt;Lien de l\'annonce: {ITEM_URL}&lt;br /&gt;&lt;br /&gt;Valider votre annonce: {VALIDATION_LINK}&lt;/p&gt;\r\n&lt;p&gt;&nbsp;&lt;/p&gt;\r\n&lt;p&gt;{WEB_TITLE}&lt;/p&gt;'),
(12, 'fr_FR', '{WEB_TITLE} - Nouvelles annonces', '&lt;div&gt;\r\n&lt;p&gt;Cher administrateur {WEB_TITLE},&lt;/p&gt;\r\n&lt;p&gt;Vous recevez ce courriel car une annonce a &eacute;t&eacute; publi&eacute;e sur {WEB_TITLE}.&lt;/p&gt;\r\n&lt;p&gt;D&eacute;tails de l\'annonce:&lt;/p&gt;\r\n&lt;p&gt;Contact: {USER_NAME}&lt;/p&gt;\r\n&lt;p&gt;Courriel du vendeur: {USER_EMAIL}&lt;/p&gt;\r\n&lt;p&gt;{ITEM_DESCRIPTION_ALL_LANGUAGES}&lt;/p&gt;\r\n&lt;p&gt;Prix: {ITEM_PRICE}&lt;br /&gt;R&eacute;gion (Province): {ITEM_REGION}&lt;br /&gt;Ville: {ITEM_CITY}&lt;br /&gt;Url: {ITEM_URL}&lt;br /&gt;&lt;br /&gt;Vous pouvez modifier votre annonce en suivant ce lien: {EDIT_LINK}&lt;/p&gt;\r\n&lt;p&gt;Merci,&lt;/p&gt;\r\n&lt;p&gt;{WEB_TITLE}&lt;/p&gt;\r\n&lt;/div&gt;'),
(13, 'fr_FR', '{WEB_TITLE} - R&eacute;cup&eacute;ration de votre mot de passe', '&lt;p&gt;Bonjour {USER_NAME},&lt;/p&gt;\r\n&lt;p&gt;Nous vous avons envoy&eacute; ce courriel parce que vous avez demand&eacute; la r&eacute;cup&eacute;ration de mot de passe. Cliquez sur ce lien pour le r&eacute;cup&eacute;rer: {PASSWORD_LINK}&lt;/p&gt;\r\n&lt;p&gt;Le lien sera d&eacute;sactiv&eacute; dans les 24 heures..&lt;/p&gt;\r\n&lt;p&gt;Si vous n\'avez pas oubli&eacute; votre mot de passe, ignorez ce message. Cette demande a &eacute;t&eacute; faite via l\'IP {IP_ADDRESS} &agrave; {DATE_TIME}&lt;/p&gt;\r\n&lt;p&gt;&nbsp;&lt;/p&gt;\r\n&lt;p&gt;{WEB_TITLE}&lt;/p&gt;'),
(14, 'fr_FR', '{WEB_TITLE} - Vous avez demand&eacute; un changement de courriel', '&lt;p&gt;Bonjour {USER_NAME}&lt;/p&gt;\r\n&lt;p&gt;Vous recevez ce courriel parce que vous avez demand&eacute; un changement de votre adresse courriel. Merci de confirmer cette nouvelle adresse courriel en cliquant sur le lien de validation suivant: {VALIDATION_LINK}&lt;/p&gt;\r\n&lt;p&gt;&nbsp;&lt;/p&gt;\r\n&lt;p&gt;{WEB_TITLE}&lt;/p&gt;'),
(15, 'fr_FR', 'Merci de confirmer votre inscription &agrave; l\'alerte', '&lt;p&gt;Bonjour {USER_NAME},&lt;/p&gt;\r\n&lt;p&gt; Valider votre inscription &agrave; l\'alerte en cliquant sur le lien suivant: {VALIDATION_LINK}&lt;/p&gt;\r\n&lt;p&gt;Merci !&lt;/p&gt;\r\n&lt;p&gt;{WEB_TITLE}&lt;/p&gt;'),
(16, 'fr_FR', '{WEB_TITLE} - Votre commentaire a &eacute;t&eacute; approuv&eacute;', '&lt;p&gt;Bonjour {COMMENT_AUTHOR},&lt;/p&gt;\r\n&lt;p&gt;Votre commentaire a &eacute;t&eacute; approuv&eacute; sur l\'annonce suivante: {ITEM_URL}&lt;/p&gt;\r\n&lt;p&gt;&nbsp;&lt;/p&gt;\r\n&lt;p&gt;{WEB_TITLE}&lt;/p&gt;')
